import axios from 'axios';
import { API_CONFIG } from '../config/api';

const authApi = axios.create({
    baseURL: `${API_CONFIG.SERVICES.AUTH}/api/${API_CONFIG.VERSION}`,
    headers: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
});

// 認證服務的 API 方法
export const authService = {
    login: (credentials) => authApi.post(API_CONFIG.ENDPOINTS.AUTH.LOGIN, credentials),
    register: (userData) => authApi.post(API_CONFIG.ENDPOINTS.AUTH.REGISTER, userData),
    getPreferences: () => authApi.get(API_CONFIG.ENDPOINTS.AUTH.PREFERENCES),
    updatePreferences: (data) => authApi.put(API_CONFIG.ENDPOINTS.AUTH.PREFERENCES, data),
};
