const API_CONFIG = {
    // 基礎 URL
    BASE_URL: process.env.REACT_APP_API_URL || 'https://ordermanagersystem-auth-service.onrender.com',

    // API 版本
    VERSION: 'v1',

    // API 端點
    ENDPOINTS: {
        AUTH: {
            LOGIN: '/auth/login',
            REGISTER: '/auth/register',
            REFRESH: '/auth/refresh',
        },
        USER: {
            PREFERENCES: '/user/preferences',
            PROFILE: '/user/profile',
            ADDRESSES: '/user/addresses',
        }
    },

    // 請求超時設置
    TIMEOUT: 10000,

    // 請求頭設置
    HEADERS: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
};

// 構建完整的 API URL
export const getApiUrl = (endpoint) => `${API_CONFIG.BASE_URL}/api/${API_CONFIG.VERSION}${endpoint}`;

// 創建 axios 實例的配置
export const axiosConfig = {
    baseURL: `${API_CONFIG.BASE_URL}/api/${API_CONFIG.VERSION}`,
    timeout: API_CONFIG.TIMEOUT,
    headers: API_CONFIG.HEADERS,
};

export default API_CONFIG;
