import axios from 'axios';

const API_BASE_URL = 'https://ordermanagersystem-auth-service.onrender.com/api/v1';

// 創建 Axios 實例
const axiosInstance = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
    },
    // 移除 withCredentials，除非您確實需要發送 cookies
    // withCredentials: true
});

// 請求攔截器
axiosInstance.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        // 添加 CORS 相關的頭
        config.headers['Access-Control-Allow-Origin'] = 'http://localhost:3000';
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// 響應攔截器
axiosInstance.interceptors.response.use(
    (response) => response,
    (error) => {
        if (error.response) {
            console.error('Response error:', error.response);
        } else if (error.request) {
            console.error('Request error:', error.request);
        } else {
            console.error('Error:', error.message);
        }
        return Promise.reject(error);
    }
);

// 認證服務的 API 方法
export const authService = {
    login: (credentials) => axiosInstance.post('/auth/login', credentials),
    register: (userData) => axiosInstance.post('/auth/register', userData),
    getPreferences: async () => {
        try {
            const response = await axiosInstance.get('/user/preferences');
            return response.data;
        } catch (error) {
            console.error('獲取用戶偏好設置失敗:', error);
            throw error;
        }
    },
    updatePreferences: async (preferences) => {
        try {
            const response = await axiosInstance.put('/user/preferences', preferences);
            return response.data;
        } catch (error) {
            console.error('更新用戶偏好設置失敗:', error);
            throw error;
        }
    },
    getAddresses: async () => {
        try {
            const response = await axiosInstance.get('/user/addresses');
            return response.data;
        } catch (error) {
            console.error('獲取地址列表失敗:', error);
            throw error;
        }
    },
    createAddress: async (addressData) => {
        try {
            const response = await axiosInstance.post('/user/addresses', addressData);
            return response.data;
        } catch (error) {
            console.error('創建地址失敗:', error);
            throw error;
        }
    },
    updateAddress: async (id, addressData) => {
        try {
            const response = await axiosInstance.put(`/user/addresses/${id}`, addressData);
            return response.data;
        } catch (error) {
            console.error('更新地址失敗:', error);
            throw error;
        }
    },
    deleteAddress: async (id) => {
        try {
            const response = await axiosInstance.delete(`/user/addresses/${id}`);
            return response.data;
        } catch (error) {
            console.error('刪除地址失敗:', error);
            throw error;
        }
    },
};
