import React, { useState, useEffect } from 'react';
import {
    Container,
    Paper,
    Typography,
    Box,
    IconButton,
    Button,
    Divider,
    Grid,
    Card,
    CardContent,
    TextField,
    Alert,
    Collapse,
    Fade,
} from '@mui/material';
import {
    Add as AddIcon,
    Remove as RemoveIcon,
    Delete as DeleteIcon,
    ShoppingCart as CartIcon,
    LocalShipping as ShippingIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CART_SERVICE_URL = 'https://ordermanagersystem.onrender.com';

const Cart = () => {
    const navigate = useNavigate();
    const [cartItems, setCartItems] = useState([]);
    const [showAlert, setShowAlert] = useState(false);
    const [alertMessage, setAlertMessage] = useState('');
    const [alertSeverity, setAlertSeverity] = useState('success');

    // 獲取購物車資料
    const fetchCartItems = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await axios.get(
                `${CART_SERVICE_URL}/api/v1/cart`,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );
            setCartItems(response.data.items.map(item => ({
                id: item.ProductID,
                name: item.Name,
                price: item.Price,
                quantity: item.Quantity,
                image: item.Image || "https://via.placeholder.com/150",
                stock: item.StockCount
            })));
        } catch (error) {
            console.error('獲取購物車失敗:', error);
            showAlertMessage('獲取購物車失敗', 'error');
        }
    };

    // 更新商品數量
    const updateQuantity = async (id, newQuantity) => {
        try {
            const token = localStorage.getItem('userToken');
            await axios.put(
                `${CART_SERVICE_URL}/api/v1/cart/items`,
                {
                    productId: id,
                    quantity: newQuantity
                },
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            // 更新本地狀態
            setCartItems(items =>
                items.map(item =>
                    item.id === id
                        ? { ...item, quantity: Math.max(1, Math.min(newQuantity, item.stock)) }
                        : item
                )
            );
            showAlertMessage('商品數量已更新', 'success');
        } catch (error) {
            console.error('更新數量失敗:', error);
            showAlertMessage('更新數量失敗', 'error');
        }
    };

    // 移除商品
    const removeItem = async (id) => {
        try {
            const token = localStorage.getItem('userToken');
            await axios.delete(
                `${CART_SERVICE_URL}/api/v1/cart/items/${id}`,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }
            );
            setCartItems(items => items.filter(item => item.id !== id));
            showAlertMessage('商品已從購物車中移除', 'success');
        } catch (error) {
            console.error('移除商品失敗:', error);
            showAlertMessage('移除商品失敗', 'error');
        }
    };

    // 清空購物車
    const clearCart = async () => {
        try {
            const token = localStorage.getItem('userToken');
            await axios.delete(
                `${CART_SERVICE_URL}/api/v1/cart`,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }
            );
            setCartItems([]);
            showAlertMessage('購物車已清空', 'success');
        } catch (error) {
            console.error('清空購物車失敗:', error);
            showAlertMessage('清空購物車失敗', 'error');
        }
    };

    // 顯示提示訊息
    const showAlertMessage = (message, severity = 'success') => {
        setAlertMessage(message);
        setAlertSeverity(severity);
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 3000);
    };

    // 初始化時獲取購物車資料
    useEffect(() => {
        fetchCartItems();
    }, []);

    // 計算總金額
    const total = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    // 運費計算（訂單滿 2000 免運費）
    const shippingFee = total >= 2000 ? 0 : 100;

    // 前往結帳
    const handleCheckout = () => {
        navigate('/checkout');
    };

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <Box sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                mb: 2
            }}>
                <Typography variant="h4" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <CartIcon /> 購物車
                </Typography>
                {cartItems.length > 0 && (
                    <Button
                        variant="outlined"
                        color="error"
                        onClick={clearCart}
                        startIcon={<DeleteIcon />}
                    >
                        清空購物車
                    </Button>
                )}
            </Box>

            <Grid container spacing={3}>
                <Grid item xs={12} md={8}>
                    <Collapse in={showAlert}>
                        <Alert
                            severity={alertSeverity}
                            sx={{ mb: 2 }}
                            onClose={() => setShowAlert(false)}
                        >
                            {alertMessage}
                        </Alert>
                    </Collapse>

                    {cartItems.length > 0 ? (
                        cartItems.map((item) => (
                            <Fade in={true} key={item.id}>
                                <Card sx={{ mb: 2, position: 'relative' }}>
                                    <CardContent>
                                        <Grid container spacing={2} alignItems="center">
                                            <Grid item xs={12} sm={3}>
                                                <img
                                                    src={item.image}
                                                    alt={item.name}
                                                    style={{
                                                        width: '100%',
                                                        height: 'auto',
                                                        borderRadius: '8px'
                                                    }}
                                                />
                                            </Grid>
                                            <Grid item xs={12} sm={9}>
                                                <Box sx={{
                                                    display: 'flex',
                                                    justifyContent: 'space-between',
                                                    alignItems: 'flex-start',
                                                    mb: 2
                                                }}>
                                                    <Typography variant="h6" component="div">
                                                        {item.name}
                                                    </Typography>
                                                    <IconButton
                                                        color="error"
                                                        onClick={() => removeItem(item.id)}
                                                        size="small"
                                                    >
                                                        <DeleteIcon />
                                                    </IconButton>
                                                </Box>
                                                <Box sx={{
                                                    display: 'flex',
                                                    justifyContent: 'space-between',
                                                    alignItems: 'center'
                                                }}>
                                                    <Box sx={{
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        gap: 1
                                                    }}>
                                                        <IconButton
                                                            size="small"
                                                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                                                            disabled={item.quantity <= 1}
                                                        >
                                                            <RemoveIcon />
                                                        </IconButton>
                                                        <TextField
                                                            size="small"
                                                            value={item.quantity}
                                                            onChange={(e) => {
                                                                const value = parseInt(e.target.value) || 1;
                                                                updateQuantity(item.id, value);
                                                            }}
                                                            inputProps={{
                                                                min: 1,
                                                                max: item.stock,
                                                                style: { textAlign: 'center', width: '50px' }
                                                            }}
                                                        />
                                                        <IconButton
                                                            size="small"
                                                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                                                            disabled={item.quantity >= item.stock}
                                                        >
                                                            <AddIcon />
                                                        </IconButton>
                                                    </Box>
                                                    <Typography variant="h6" color="primary">
                                                        NT$ {item.price * item.quantity}
                                                    </Typography>
                                                </Box>
                                                <Typography variant="caption" color="text.secondary">
                                                    庫存: {item.stock} 件
                                                </Typography>
                                            </Grid>
                                        </Grid>
                                    </CardContent>
                                </Card>
                            </Fade>
                        ))
                    ) : (
                        <Paper sx={{ p: 3, textAlign: 'center' }}>
                            <Typography variant="h6" color="text.secondary">
                                購物車是空的
                            </Typography>
                            <Button
                                variant="contained"
                                sx={{ mt: 2 }}
                                onClick={() => navigate('/products')}
                            >
                                繼續購物
                            </Button>
                        </Paper>
                    )}
                </Grid>

                <Grid item xs={12} md={4}>
                    <Paper sx={{ p: 3, position: 'sticky', top: 20 }}>
                        <Typography variant="h6" gutterBottom>
                            訂單摘要
                        </Typography>
                        <Box sx={{ my: 2 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                <Typography>商品總計</Typography>
                                <Typography>NT$ {total}</Typography>
                            </Box>
                            <Box sx={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                mb: 1,
                                alignItems: 'center'
                            }}>
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                    <ShippingIcon fontSize="small" />
                                    <Typography>運費</Typography>
                                </Box>
                                <Typography>
                                    {shippingFee === 0 ? '免運費' : `NT$ ${shippingFee}`}
                                </Typography>
                            </Box>
                            {total < 2000 && (
                                <Typography variant="caption" color="primary">
                                    還差 NT$ {2000 - total} 即可享有免運優惠
                                </Typography>
                            )}
                        </Box>
                        <Divider sx={{ my: 2 }} />
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                            <Typography variant="h6">總計</Typography>
                            <Typography variant="h6" color="primary">
                                NT$ {total + shippingFee}
                            </Typography>
                        </Box>
                        <Button
                            variant="contained"
                            fullWidth
                            size="large"
                            onClick={handleCheckout}
                            disabled={cartItems.length === 0}
                        >
                            前往結帳
                        </Button>
                    </Paper>
                </Grid>
            </Grid>
        </Container>
    );
};

export default Cart;
