export const API_CONFIG = {
    BASE_URL: process.env.REACT_APP_API_URL || 'https://ordermanagersystem-auth-service.onrender.com',
    API_VERSION: '/api/v1',
    getFullUrl: function () {
        // 確保 BASE_URL 不以斜線結尾，API_VERSION 以斜線開頭
        const baseUrl = this.BASE_URL.endsWith('/') ? this.BASE_URL.slice(0, -1) : this.BASE_URL;
        const version = this.API_VERSION.startsWith('/') ? this.API_VERSION : `/${this.API_VERSION}`;
        return `${baseUrl}${version}`;
    }
};

// 創建 API 路徑常量
export const API_ENDPOINTS = {
    AUTH: {
        LOGIN: '/auth/login',
        REGISTER: '/auth/register',
        FORGOT_PASSWORD: '/auth/forgot-password',
    },
    USER: {
        PREFERENCES: '/user/preferences',
    },
    // 可以繼續添加其他 API 端點
};
