import React, { useState, useEffect } from 'react';
import {
    Container,
    Paper,
    Typography,
    Box,
    Switch,
    FormControlLabel,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    Button,
    Divider,
    Alert,
    CircularProgress,
    useTheme
} from '@mui/material';
import {
    Language as LanguageIcon,
    MonetizationOn as CurrencyIcon,
    Notifications as NotificationsIcon,
    Palette as PaletteIcon
} from '@mui/icons-material';
import axios from 'axios';
import { API_CONFIG, API_ENDPOINTS } from '../../config/api.config';

const api = axios.create({
    baseURL: API_CONFIG.getFullUrl(),
    headers: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
});

const Preferences = () => {
    const theme = useTheme();
    const [preferences, setPreferences] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        fetchPreferences();
    }, []);

    const fetchPreferences = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await api.get(API_ENDPOINTS.USER.PREFERENCES, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setPreferences(response.data);
        } catch (err) {
            setError('無法載入個人設定');
            console.error('Error fetching preferences:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleChange = async (field, value) => {
        try {
            const token = localStorage.getItem('userToken');
            const updatedPreferences = { ...preferences, [field]: value };

            await api.put(API_ENDPOINTS.USER.PREFERENCES, updatedPreferences, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            setPreferences(updatedPreferences);
            setSuccess('設定已更新');
            setTimeout(() => setSuccess(''), 3000);
        } catch (err) {
            setError('更新設定失敗');
            setTimeout(() => setError(''), 3000);
        }
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Container maxWidth="md" sx={{ py: 4 }}>
            <Paper elevation={3} sx={{ p: 4 }}>
                <Typography variant="h4" gutterBottom sx={{ mb: 4 }}>
                    個人設定
                </Typography>

                {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <LanguageIcon color="primary" />
                        <FormControl fullWidth>
                            <InputLabel>語言</InputLabel>
                            <Select
                                value={preferences?.language || 'zh-TW'}
                                label="語言"
                                onChange={(e) => handleChange('language', e.target.value)}
                            >
                                <MenuItem value="zh-TW">繁體中文</MenuItem>
                                <MenuItem value="en-US">English</MenuItem>
                            </Select>
                        </FormControl>
                    </Box>

                    <Divider />

                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <CurrencyIcon color="primary" />
                        <FormControl fullWidth>
                            <InputLabel>貨幣</InputLabel>
                            <Select
                                value={preferences?.currency || 'TWD'}
                                label="貨幣"
                                onChange={(e) => handleChange('currency', e.target.value)}
                            >
                                <MenuItem value="TWD">新台幣 (TWD)</MenuItem>
                                <MenuItem value="USD">美元 (USD)</MenuItem>
                            </Select>
                        </FormControl>
                    </Box>

                    <Divider />

                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <NotificationsIcon color="primary" />
                        <Box sx={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
                            <FormControlLabel
                                control={
                                    <Switch
                                        checked={preferences?.notification_email || false}
                                        onChange={(e) => handleChange('notification_email', e.target.checked)}
                                    />
                                }
                                label="電子郵件通知"
                            />
                            <FormControlLabel
                                control={
                                    <Switch
                                        checked={preferences?.notification_sms || false}
                                        onChange={(e) => handleChange('notification_sms', e.target.checked)}
                                    />
                                }
                                label="簡訊通知"
                            />
                        </Box>
                    </Box>

                    <Divider />

                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <PaletteIcon color="primary" />
                        <FormControl fullWidth>
                            <InputLabel>主題</InputLabel>
                            <Select
                                value={preferences?.theme || 'light'}
                                label="主題"
                                onChange={(e) => handleChange('theme', e.target.value)}
                            >
                                <MenuItem value="light">淺色主題</MenuItem>
                                <MenuItem value="dark">深色主題</MenuItem>
                            </Select>
                        </FormControl>
                    </Box>
                </Box>
            </Paper>
        </Container>
    );
};

export default Preferences;
