    Box,
    Avatar,
    List,
    ListItem,
    ListItemText,
    Divider,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Tabs,
    Tab,
    Switch,
    FormControlLabel,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    Alert,
    CircularProgress,
} from '@mui/material';
import {
    Language as LanguageIcon,
    MonetizationOn as CurrencyIcon,
    Notifications as NotificationsIcon,
    Palette as PaletteIcon,
    Person as PersonIcon,
    Settings as SettingsIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_CONFIG, API_ENDPOINTS } from '../../config/api.config';

const api = axios.create({
    baseURL: API_CONFIG.getFullUrl(),
    headers: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
});

// TabPanel 組件
function TabPanel({ children, value, index, ...other }) {
    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`profile-tabpanel-${index}`}
            aria-labelledby={`profile-tab-${index}`}
            {...other}
        >
            {value === index && <Box sx={{ pt: 3 }}>{children}</Box>}
        </div>
    );
}

const Profile = () => {
    const navigate = useNavigate();
    const [tabValue, setTabValue] = useState(0);
    const [user, setUser] = useState(null);
    const [openEditDialog, setOpenEditDialog] = useState(false);
    const [editForm, setEditForm] = useState({
        username: '',
        email: '',
    });
    const [preferences, setPreferences] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        const userData = localStorage.getItem('userData');
        if (!userData) {
            navigate('/login');
            return;
        }
        setUser(JSON.parse(userData));
        setEditForm({
            username: JSON.parse(userData).username,
            email: JSON.parse(userData).email,
        });
        fetchPreferences();
    }, [navigate]);
    const fetchPreferences = async () => {
        const token = localStorage.getItem('userToken');
        if (!token) {
            setError('請先登入');
            setLoading(false);
            return;
        }

        try {
            console.log('Fetching preferences from:', API_CONFIG.getFullUrl() + API_ENDPOINTS.USER.PREFERENCES);
            const response = await api.get(API_ENDPOINTS.USER.PREFERENCES, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Origin': window.location.origin
                }
            });
            console.log('Preferences response:', response.data);
            setPreferences(response.data);
        } catch (err) {
            console.error('Full error:', err);
            if (err.response?.status === 401) {
                setError('登入已過期，請重新登入');
                navigate('/login');
            } else {
                setError(`無法載入個人設定: ${err.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    const handlePreferenceChange = async (field, value) => {
        try {
            const token = localStorage.getItem('userToken');
            const updatedPreferences = { ...preferences, [field]: value };

            await api.put(API_ENDPOINTS.USER.PREFERENCES, updatedPreferences, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Origin': window.location.origin
                }
            });

            setPreferences(updatedPreferences);
            setSuccess('設定已更新');
            setTimeout(() => setSuccess(''), 3000);
        } catch (err) {
            setError('更新設定失敗');
            setTimeout(() => setError(''), 3000);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('userToken');
        localStorage.removeItem('userData');
        navigate('/login');
    };

    const handleEditClick = () => {
        setOpenEditDialog(true);
    };

    const handleEditClose = () => {
        setOpenEditDialog(false);
    };

    const handleEditSubmit = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await axios.put(
                `${API_CONFIG.getFullUrl()}/users/${user.id}`,
                editForm,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data) {
                const updatedUser = { ...user, ...editForm };
                localStorage.setItem('userData', JSON.stringify(updatedUser));
                setUser(updatedUser);
                setOpenEditDialog(false);
                setSuccess('個人資料已更新');
                setTimeout(() => setSuccess(''), 3000);
            }
        } catch (error) {
            console.error('更新個人資料失敗:', error);
            setError('更新個人資料失敗');
            setTimeout(() => setError(''), 3000);
        }
    };

    const handleTabChange = (event, newValue) => {
        setTabValue(newValue);
    };

    if (!user || loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
            <Paper elevation={3} sx={{ p: 4 }}>
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                    <Tabs value={tabValue} onChange={handleTabChange} aria-label="profile tabs">
                        <Tab icon={<PersonIcon />} label="個人資料" />
                        <Tab icon={<SettingsIcon />} label="個人設定" />
                    </Tabs>
                </Box>

                {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
                {success && <Alert severity="success" sx={{ mt: 2 }}>{success}</Alert>}

                <TabPanel value={tabValue} index={0}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
                        <Avatar
                            sx={{
                                width: 100,
                                height: 100,
                                mr: 3,
                                bgcolor: 'primary.main',
                                fontSize: '2rem',
                            }}
                        >
                            {user.username?.[0]?.toUpperCase()}
                        </Avatar>
                        <Box>
                            <Typography variant="h4" gutterBottom>
                                {user.username}
                            </Typography>
                            <Typography variant="subtitle1" color="text.secondary">
                                {user.email}
                            </Typography>
                        </Box>
                    </Box>

                    <List>
                        <ListItem>
                            <ListItemText
                                primary="會員角色"
                                secondary={user.role === 'admin' ? '管理員' : '一般會員'}
                            />
                        </ListItem>
                        <Divider />
                        <ListItem>
                            <ListItemText
                                primary="帳號狀態"
                                secondary={user.status === 'active' ? '正常' : '停用'}
                            />
                        </ListItem>
                        <Divider />
                        <ListItem>
                            <ListItemText
                                primary="註冊時間"
                                secondary={new Date(user.created_at).toLocaleString()}
                            />
                        </ListItem>
                    </List>

                    <Box sx={{ mt: 4, display: 'flex', gap: 2 }}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleEditClick}
                        >
                            編輯資料
                        </Button>
                        <Button
                            variant="outlined"
                            color="error"
                            onClick={handleLogout}
                        >
                            登出
                        </Button>
                    </Box>
                </TabPanel>

                <TabPanel value={tabValue} index={1}>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <LanguageIcon color="primary" />
                            <FormControl fullWidth>
                                <InputLabel>語言</InputLabel>
                                <Select
                                    value={preferences?.language || 'zh-TW'}
                                    label="語言"
                                    onChange={(e) => handlePreferenceChange('language', e.target.value)}
                                >
                                    <MenuItem value="zh-TW">繁體中文</MenuItem>
                                    <MenuItem value="en-US">English</MenuItem>
                                </Select>
                            </FormControl>
                        </Box>

                        <Divider />

                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <CurrencyIcon color="primary" />
                            <FormControl fullWidth>
                                <InputLabel>貨幣</InputLabel>
                                <Select
                                    value={preferences?.currency || 'TWD'}
                                    label="貨幣"
                                    onChange={(e) => handlePreferenceChange('currency', e.target.value)}
                                >
                                    <MenuItem value="TWD">新台幣 (TWD)</MenuItem>
                                    <MenuItem value="USD">美元 (USD)</MenuItem>
                                </Select>
                            </FormControl>
                        </Box>

                        <Divider />

                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <NotificationsIcon color="primary" />
                            <Box sx={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
                                <FormControlLabel
                                    control={
                                        <Switch
                                            checked={preferences?.notification_email || false}
                                            onChange={(e) => handlePreferenceChange('notification_email', e.target.checked)}
                                        />
                                    }
                                    label="電子郵件通知"
                                />
                                <FormControlLabel
                                    control={
                                        <Switch
                                            checked={preferences?.notification_sms || false}
                                            onChange={(e) => handlePreferenceChange('notification_sms', e.target.checked)}
                                        />
                                    }
                                    label="簡訊通知"
                                />
                            </Box>
                        </Box>

                        <Divider />

                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <PaletteIcon color="primary" />
                            <FormControl fullWidth>
                                <InputLabel>主題</InputLabel>
                                <Select
                                    value={preferences?.theme || 'light'}
                                    label="主題"
                                    onChange={(e) => handlePreferenceChange('theme', e.target.value)}
                                >
                                    <MenuItem value="light">淺色主題</MenuItem>
                                    <MenuItem value="dark">深色主題</MenuItem>
                                </Select>
                            </FormControl>
                        </Box>
                    </Box>
                </TabPanel>
            </Paper>

            <Dialog open={openEditDialog} onClose={handleEditClose}>
                <DialogTitle>編輯個人資料</DialogTitle>
                <DialogContent>
                    <TextField
                        autoFocus
                        margin="dense"
                        label="用戶名稱"
                        type="text"
                        fullWidth
                        value={editForm.username}
                        onChange={(e) =>
                            setEditForm({ ...editForm, username: e.target.value })
                        }
                    />
                    <TextField
                        margin="dense"
                        label="電子郵件"
                        type="email"
                        fullWidth
                        value={editForm.email}
                        onChange={(e) =>
                            setEditForm({ ...editForm, email: e.target.value })
                        }
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleEditClose}>取消</Button>
                    <Button onClick={handleEditSubmit} variant="contained">
                        儲存
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default Profile;
