import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Container,
    Grid,
    Paper,
    Typography,
    Box,
    Avatar,
    Divider,
    Button,
    Switch,
    FormControlLabel,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    Alert,
    Snackbar,
    Tab,
    Tabs,
    CircularProgress,
} from '@mui/material';
import {
    Person,
    Settings,
    Notifications,
    Language,
    CurrencyExchange,
    DarkMode,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const API_URL = process.env.REACT_APP_API_URL || 'https://ordermanagersystem-auth-service.onrender.com';

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
});

// 設定面板內容組件
function TabPanel({ children, value, index }) {
    return (
        <div role="tabpanel" hidden={value !== index}>
            {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
        </div>
    );
}

const Profile = () => {
    const navigate = useNavigate();
    const { user, logout } = useAuth();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [tabValue, setTabValue] = useState(0);
    const [preferences, setPreferences] = useState({
        language: 'zh-TW',
        currency: 'TWD',
        theme: 'light',
        notification_email: true,
        notification_sms: false,
    });

    useEffect(() => {
        fetchUserPreferences();
    }, []);

    const fetchUserPreferences = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await api.get('/api/v1/user/preferences', {
                headers: {
                    Authorization: `Bearer ${token}`,
                }
            });
            setPreferences(response.data);
            setLoading(false);
        } catch (err) {
            console.error('獲取用戶設定失敗:', err);
            setError('無法載入用戶設定');
            setLoading(false);
        }
    };

    const handlePreferencesChange = async () => {
        try {
            const token = localStorage.getItem('userToken');
            await api.put('/api/v1/user/preferences', preferences, {
                headers: {
                    Authorization: `Bearer ${token}`,
                }
            });
            setSuccess('設定已更新');
        } catch (err) {
            console.error('更新用戶設定失敗:', err);
            setError('無法更新設定');
        }
    };

    const handleTabChange = (event, newValue) => {
        setTabValue(newValue);
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <Grid container spacing={3}>
                {/* 個人資料卡片 */}
                <Grid item xs={12} md={4}>
                    <Paper
                        sx={{
                            p: 3,
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}
                    >
                        <Avatar
                            sx={{
                                width: 100,
                                height: 100,
                                mb: 2,
                                bgcolor: 'primary.main',
                                fontSize: '2.5rem',
                            }}
                        >
                            {user?.username?.[0]?.toUpperCase()}
                        </Avatar>
                        <Typography variant="h5" gutterBottom>
                            {user?.username}
                        </Typography>
                        <Typography color="text.secondary" gutterBottom>
                            {user?.email}
                        </Typography>
                        <Typography
                            variant="body2"
                            color="text.secondary"
                            sx={{ mb: 2 }}
                        >
                            {user?.role === 'admin' ? '管理員' : '一般會員'}
                        </Typography>
                        <Button
                            variant="outlined"
                            color="error"
                            onClick={handleLogout}
                            sx={{ mt: 2 }}
                        >
                            登出
                        </Button>
                    </Paper>
                </Grid>

                {/* 設定面板 */}
                <Grid item xs={12} md={8}>
                    <Paper sx={{ width: '100%' }}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                            <Tabs
                                value={tabValue}
                                onChange={handleTabChange}
                                aria-label="設定選項"
                            >
                                <Tab
                                    icon={<Settings />}
                                    label="一般設定"
                                    iconPosition="start"
                                />
                                <Tab
                                    icon={<Notifications />}
                                    label="通知設定"
                                    iconPosition="start"
                                />
                            </Tabs>
                        </Box>

                        <TabPanel value={tabValue} index={0}>
                            <Grid container spacing={3}>
                                <Grid item xs={12} sm={6}>
                                    <FormControl fullWidth>
                                        <InputLabel>語言</InputLabel>
                                        <Select
                                            value={preferences.language}
                                            label="語言"
                                            onChange={(e) =>
                                                setPreferences({
                                                    ...preferences,
                                                    language: e.target.value,
                                                })
                                            }
                                        >
                                            <MenuItem value="zh-TW">繁體中文</MenuItem>
                                            <MenuItem value="en-US">English</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <FormControl fullWidth>
                                        <InputLabel>貨幣</InputLabel>
                                        <Select
                                            value={preferences.currency}
                                            label="貨幣"
                                            onChange={(e) =>
                                                setPreferences({
                                                    ...preferences,
                                                    currency: e.target.value,
                                                })
                                            }
                                        >
                                            <MenuItem value="TWD">新台幣 (TWD)</MenuItem>
                                            <MenuItem value="USD">美元 (USD)</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid>
                                <Grid item xs={12}>
                                    <FormControlLabel
                                        control={
                                            <Switch
                                                checked={preferences.theme === 'dark'}
                                                onChange={(e) =>
                                                    setPreferences({
                                                        ...preferences,
                                                        theme: e.target.checked ? 'dark' : 'light',
                                                    })
                                                }
                                            />
                                        }
                                        label="深色模式"
                                    />
                                </Grid>
                            </Grid>
                        </TabPanel>

                        <TabPanel value={tabValue} index={1}>
                            <Grid container spacing={3}>
                                <Grid item xs={12}>
                                    <FormControlLabel
                                        control={
                                            <Switch
                                                checked={preferences.notification_email}
                                                onChange={(e) =>
                                                    setPreferences({
                                                        ...preferences,
                                                        notification_email: e.target.checked,
                                                    })
                                                }
                                            />
                                        }
                                        label="接收電子郵件通知"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <FormControlLabel
                                        control={
                                            <Switch
                                                checked={preferences.notification_sms}
                                                onChange={(e) =>
                                                    setPreferences({
                                                        ...preferences,
                                                        notification_sms: e.target.checked,
                                                    })
                                                }
                                            />
                                        }
                                        label="接收簡訊通知"
                                    />
                                </Grid>
                            </Grid>
                        </TabPanel>

                        <Box sx={{ p: 3, display: 'flex', justifyContent: 'flex-end' }}>
                            <Button
                                variant="contained"
                                onClick={handlePreferencesChange}
                            >
                                儲存設定
                            </Button>
                        </Box>
                    </Paper>
                </Grid>
            </Grid>

            <Snackbar
                open={!!error}
                autoHideDuration={6000}
                onClose={() => setError('')}
            >
                <Alert severity="error" onClose={() => setError('')}>
                    {error}
                </Alert>
            </Snackbar>

            <Snackbar
                open={!!success}
                autoHideDuration={6000}
                onClose={() => setSuccess('')}
            >
                <Alert severity="success" onClose={() => setSuccess('')}>
                    {success}
                </Alert>
            </Snackbar>
        </Container>
    );
};

export default Profile;
