import axios from 'axios';

// const API_URL = 'https://ordermanagersystem-auth-service.onrender.com';
const API_URL = 'http://localhost:8083';

// 創建 axios 實例
const axiosInstance = axios.create({
    baseURL: `${API_URL}/api/v1`,
    withCredentials: true,
    headers: {
        'Content-Type': 'application/json',
    }
});

// 請求攔截器，添加 token
axiosInstance.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token'); // 或者從您的 auth context 獲取
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// 響應攔截器
axiosInstance.interceptors.response.use(
    (response) => response,
    async (error) => {
        const originalRequest = error.config;

        // 如果是 401 錯誤且不是刷新 token 的請求
        if (error.response?.status === 401 && !originalRequest._retry && !originalRequest.url.includes('/auth/refresh')) {
            originalRequest._retry = true;

            try {
                // 嘗試刷新 token
                const response = await axiosInstance.post('/auth/refresh');
                const { token } = response.data;

                // 更新本地存儲的 token
                localStorage.setItem('token', token);

                // 更新當前請求的 Authorization header
                originalRequest.headers.Authorization = `Bearer ${token}`;

                // 重試原始請求
                return axiosInstance(originalRequest);
            } catch (refreshError) {
                // 如果刷新 token 失敗，清除本地存儲並重定向到登錄頁
                localStorage.removeItem('token');
                // window.location.href = '/login';
                return Promise.reject(refreshError);
            }
        }

        // 其他錯誤處理
        if (error.response) {
            console.error('Response error:', error.response);
        } else if (error.request) {
            console.error('Request error:', error.request);
        } else {
            console.error('Error:', error.message);
        }
        return Promise.reject(error);
    }
);

// 認證服務的 API 方法
export const authService = {
    login: (credentials) => axiosInstance.post('/auth/login', credentials),
    register: (userData) => axiosInstance.post('/auth/register', userData),
    getPreferences: () => axiosInstance.get('/user/preferences'),
    updatePreferences: async (preferences) => {
        try {
            const response = await axiosInstance.put('/user/preferences', preferences);
            return response;
        } catch (error) {
            console.error('更新用戶偏好設置失敗:', error);
            throw error;
        }
    },
    getAddresses: () => axiosInstance.get('/user/addresses'),
    createAddress: async (addressData) => {
        try {
            const response = await axiosInstance.post('/user/addresses', addressData);
            return response;
        } catch (error) {
            console.error('創建地址失敗:', error);
            throw error;
        }
    },
    updateAddress: async (id, addressData) => {
        try {
            const response = await axiosInstance.put(`/user/addresses/${id}`, addressData);
            return response;
        } catch (error) {
            console.error('更新地址失敗:', error);
            throw error;
        }
    },
    deleteAddress: async (id) => {
        try {
            const response = await axiosInstance.delete(`/user/addresses/${id}`);
            return response;
        } catch (error) {
            console.error('刪除地址失敗:', error);
            throw error;
        }
    },
};
