import React, { useState, useEffect } from 'react';
import {
    Container,
    Paper,
    Typography,
    Box,
    Avatar,
    List,
    ListItem,
    ListItemText,
    Divider,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Grid,
    Switch,
    FormGroup,
    FormControlLabel,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import SaveIcon from '@mui/icons-material/Save';

const AUTH_SERVICE_URL = process.env.AUTH_SERVICE_URL || 'https://ordermanagersystem-auth-service.onrender.com';

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [openEditDialog, setOpenEditDialog] = useState(false);
    const [editForm, setEditForm] = useState({
        username: '',
        email: '',
    });
    const [preferences, setPreferences] = useState(null);
    const [addresses, setAddresses] = useState([]);
    const [openPreferencesDialog, setOpenPreferencesDialog] = useState(false);
    const [preferencesForm, setPreferencesForm] = useState({
        language: 'zh_TW',
        currency: 'TWD',
        theme: 'light',
        notification_email: true,
        notification_sms: false
    });

    useEffect(() => {
        const userData = localStorage.getItem('userData');
        if (!userData) {
            navigate('/login');
            return;
        }
        setUser(JSON.parse(userData));
        setEditForm({
            username: JSON.parse(userData).username,
            email: JSON.parse(userData).email,
        });
        fetchUserData();
    }, [navigate]);

    const fetchUserData = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const headers = {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            };

            // 獲取用戶偏好設定
            const preferencesResponse = await axios.get(
                `${AUTH_SERVICE_URL}/api/v1/user/preferences`,
                {
                    headers,
                    withCredentials: true  // 如果需要發送 cookies
                }
            );
            setPreferences(preferencesResponse.data);
            setPreferencesForm(preferencesResponse.data);

            // 獲取用戶地址
            const addressesResponse = await axios.get(
                `${AUTH_SERVICE_URL}/api/v1/user/addresses`,
                {
                    headers,
                    withCredentials: true  // 如果需要發送 cookies
                }
            );
            setAddresses(addressesResponse.data);
        } catch (error) {
            console.error('獲取用戶資料失敗:', error);
            // 添加更詳細的錯誤處理
            if (error.response) {
                console.error('錯誤狀態:', error.response.status);
                console.error('錯誤數據:', error.response.data);
            }
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('userToken');
        localStorage.removeItem('userData');
        navigate('/login');
    };

    const handleEditClick = () => {
        setOpenEditDialog(true);
    };

    const handleEditClose = () => {
        setOpenEditDialog(false);
    };

    const handleEditSubmit = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await axios.put(
                `${AUTH_SERVICE_URL}/users/${user.id}`,
                editForm,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data) {
                const updatedUser = { ...user, ...editForm };
                localStorage.setItem('userData', JSON.stringify(updatedUser));
                setUser(updatedUser);
                setOpenEditDialog(false);
            }
        } catch (error) {
            console.error('更新個人資料失敗:', error);
        }
    };

    const handlePreferencesSubmit = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await axios.put(
                `${AUTH_SERVICE_URL}/api/v1/user/preferences`,
                preferencesForm,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data) {
                setPreferences(response.data);
                setOpenPreferencesDialog(false);
            }
        } catch (error) {
            console.error('更新偏好設定失敗:', error);
        }
    };

    if (!user) {
        return null;
    }

    return (
        <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
            <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
                    <Avatar
                        sx={{
                            width: 100,
                            height: 100,
                            mr: 3,
                            bgcolor: 'primary.main',
                            fontSize: '2rem',
                        }}
                    >
                        {user.username?.[0]?.toUpperCase()}
                    </Avatar>
                    <Box>
                        <Typography variant="h4" gutterBottom>
                            {user.username}
                        </Typography>
                        <Typography variant="subtitle1" color="text.secondary">
                            {user.email}
                        </Typography>
                    </Box>
                </Box>

                <List>
                    <ListItem>
                        <ListItemText
                            primary="會員角色"
                            secondary={user.role === 'admin' ? '管理員' : '一般會員'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="帳號狀態"
                            secondary={user.status === 'active' ? '正常' : '停用'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="註冊時間"
                            secondary={new Date(user.created_at).toLocaleString()}
                        />
                    </ListItem>
                </List>

                <Box sx={{ mt: 4, display: 'flex', gap: 2 }}>
                    <Button variant="contained" color="primary" onClick={handleEditClick}>
                        編輯資料
                    </Button>
                    <Button variant="outlined" color="error" onClick={handleLogout}>
                        登出
                    </Button>
                </Box>
            </Paper>

            <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
                <Typography variant="h5" gutterBottom sx={{ mb: 3, fontWeight: 'medium' }}>
                    個人化設定
                </Typography>

                <Grid container spacing={3}>
                    <Grid item xs={12} md={6}>
                        <Box sx={{ 
                            p: 2, 
                            border: '1px solid', 
                            borderColor: 'divider',
                            borderRadius: 1,
                            transition: 'all 0.3s',
                            '&:hover': {
                                boxShadow: 1,
                                borderColor: 'primary.main',
                            }
                        }}>
                            <Typography variant="subtitle1" gutterBottom>
                                語言
                            </Typography>
                            <TextField
                                select
                                fullWidth
                                size="small"
                                value={preferencesForm.language}
                                onChange={(e) =>
                                    setPreferencesForm({ ...preferencesForm, language: e.target.value })
                                }
                                SelectProps={{
                                    native: true,
                                }}
                            >
                                <option value="zh_TW">繁體中文</option>
                                <option value="en">English</option>
                            </TextField>
                        </Box>
                    </Grid>

                    <Grid item xs={12} md={6}>
                        <Box sx={{ 
                            p: 2, 
                            border: '1px solid', 
                            borderColor: 'divider',
                            borderRadius: 1,
                            transition: 'all 0.3s',
                            '&:hover': {
                                boxShadow: 1,
                                borderColor: 'primary.main',
                            }
                        }}>
                            <Typography variant="subtitle1" gutterBottom>
                                幣別
                            </Typography>
                            <TextField
                                select
                                fullWidth
                                size="small"
                                value={preferencesForm.currency}
                                onChange={(e) =>
                                    setPreferencesForm({ ...preferencesForm, currency: e.target.value })
                                }
                                SelectProps={{
                                    native: true,
                                }}
                            >
                                <option value="TWD">新台幣 (TWD)</option>
                                <option value="USD">美元 (USD)</option>
                            </TextField>
                        </Box>
                    </Grid>

                    <Grid item xs={12} md={6}>
                        <Box sx={{ 
                            p: 2, 
                            border: '1px solid', 
                            borderColor: 'divider',
                            borderRadius: 1,
                            transition: 'all 0.3s',
                            '&:hover': {
                                boxShadow: 1,
                                borderColor: 'primary.main',
                            }
                        }}>
                            <Typography variant="subtitle1" gutterBottom>
                                主題
                            </Typography>
                            <TextField
                                select
                                fullWidth
                                size="small"
                                value={preferencesForm.theme}
                                onChange={(e) =>
                                    setPreferencesForm({ ...preferencesForm, theme: e.target.value })
                                }
                                SelectProps={{
                                    native: true,
                                }}
                            >
                                <option value="light">淺色</option>
                                <option value="dark">深色</option>
                            </TextField>
                        </Box>
                    </Grid>

                    <Grid item xs={12} md={6}>
                        <Box sx={{ 
                            p: 2, 
                            border: '1px solid', 
                            borderColor: 'divider',
                            borderRadius: 1,
                            transition: 'all 0.3s',
                            '&:hover': {
                                boxShadow: 1,
                                borderColor: 'primary.main',
                            }
                        }}>
                            <Typography variant="subtitle1" gutterBottom>
                                通知設定
                            </Typography>
                            <FormGroup>
                                <FormControlLabel
                                    control={
                                        <Switch
                                            checked={preferencesForm.notification_email}
                                            onChange={(e) =>
                                                setPreferencesForm({
                                                    ...preferencesForm,
                                                    notification_email: e.target.checked,
                                                })
                                            }
                                        />
                                    }
                                    label="電子郵件通知"
                                />
                                <FormControlLabel
                                    control={
                                        <Switch
                                            checked={preferencesForm.notification_sms}
                                            onChange={(e) =>
                                                setPreferencesForm({
                                                    ...preferencesForm,
                                                    notification_sms: e.target.checked,
                                                })
                                            }
                                        />
                                    }
                                    label="簡訊通知"
                                />
                            </FormGroup>
                        </Box>
                    </Grid>
                </Grid>

                <Box sx={{ mt: 4, display: 'flex', justifyContent: 'flex-end' }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handlePreferencesSubmit}
                        startIcon={<SaveIcon />}
                    >
                        儲存設定
                    </Button>
                </Box>
            </Paper>

            <Paper elevation={3} sx={{ p: 4 }}>
                <Typography variant="h5" gutterBottom sx={{ mb: 3, fontWeight: 'medium' }}>
                    收貨地址
                </Typography>
                <List>
                    {addresses.map((address, index) => (
                        <ListItem key={index}>
                            <ListItemText
                                primary={`地址 ${index + 1}`}
                                secondary={address.full_address}
                            />
                        </ListItem>
                    ))}
                </List>
            </Paper>

            <Dialog open={openEditDialog} onClose={handleEditClose}>
                <DialogTitle>編輯個人資料</DialogTitle>
                <DialogContent>
                    <TextField
                        autoFocus
                        margin="dense"
                        label="用戶名稱"
                        type="text"
                        fullWidth
                        value={editForm.username}
                        onChange={(e) =>
                            setEditForm({ ...editForm, username: e.target.value })
                        }
                    />
                    <TextField
                        margin="dense"
                        label="電子郵件"
                        type="email"
                        fullWidth
                        value={editForm.email}
                        onChange={(e) =>
                            setEditForm({ ...editForm, email: e.target.value })
                        }
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleEditClose}>取消</Button>
                    <Button onClick={handleEditSubmit} variant="contained">
                        儲存
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default Profile;
