import React, { useState, useEffect } from 'react';
import {
    Container,
    Paper,
    Typography,
    Box,
    Avatar,
    List,
    ListItem,
    ListItemText,
    Divider,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'https://ordermanagersystem-auth-service.onrender.com';
const PROD_AUTH_SERVICE = process.env.REACT_APP_PROD_AUTH_SERVICE;

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [openEditDialog, setOpenEditDialog] = useState(false);
    const [editForm, setEditForm] = useState({
        username: '',
        email: '',
    });
    const [preferences, setPreferences] = useState(null);
    const [addresses, setAddresses] = useState([]);
    const [openPreferencesDialog, setOpenPreferencesDialog] = useState(false);
    const [preferencesForm, setPreferencesForm] = useState({
        language: 'zh_TW',
        currency: 'TWD',
        theme: 'light',
        notification_email: true,
        notification_sms: false
    });

    useEffect(() => {
        const userData = localStorage.getItem('userData');
        if (!userData) {
            navigate('/login');
            return;
        }
        setUser(JSON.parse(userData));
        setEditForm({
            username: JSON.parse(userData).username,
            email: JSON.parse(userData).email,
        });
        fetchUserData();
    }, [navigate]);

    const fetchUserData = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const headers = {
                Authorization: `Bearer ${token}`
            };

            // 獲取用戶偏好設定
            const preferencesResponse = await axios.get(
                `${API_URL}/api/v1/user/preferences`,
                { headers }
            );
            setPreferences(preferencesResponse.data);
            setPreferencesForm(preferencesResponse.data);

            // 獲取用戶地址
            const addressesResponse = await axios.get(
                `${PROD_AUTH_SERVICE}/api/v1/user/addresses`,
                { headers }
            );
            setAddresses(addressesResponse.data);
        } catch (error) {
            console.error('獲取用戶資料失敗:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('userToken');
        localStorage.removeItem('userData');
        navigate('/login');
    };

    const handleEditClick = () => {
        setOpenEditDialog(true);
    };

    const handleEditClose = () => {
        setOpenEditDialog(false);
    };

    const handleEditSubmit = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await axios.put(
                `${API_URL}/users/${user.id}`,
                editForm,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data) {
                const updatedUser = { ...user, ...editForm };
                localStorage.setItem('userData', JSON.stringify(updatedUser));
                setUser(updatedUser);
                setOpenEditDialog(false);
            }
        } catch (error) {
            console.error('更新個人資料失敗:', error);
        }
    };

    const handlePreferencesSubmit = async () => {
        try {
            const token = localStorage.getItem('userToken');
            const response = await axios.put(
                `${API_URL}/api/v1/user/preferences`,
                preferencesForm,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data) {
                setPreferences(response.data);
                setOpenPreferencesDialog(false);
            }
        } catch (error) {
            console.error('更新偏好設定失敗:', error);
        }
    };

    if (!user) {
        return null;
    }

    return (
        <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
            <Paper elevation={3} sx={{ p: 4 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
                    <Avatar
                        sx={{
                            width: 100,
                            height: 100,
                            mr: 3,
                            bgcolor: 'primary.main',
                            fontSize: '2rem',
                        }}
                    >
                        {user.username?.[0]?.toUpperCase()}
                    </Avatar>
                    <Box>
                        <Typography variant="h4" gutterBottom>
                            {user.username}
                        </Typography>
                        <Typography variant="subtitle1" color="text.secondary">
                            {user.email}
                        </Typography>
                    </Box>
                </Box>

                <List>
                    <ListItem>
                        <ListItemText
                            primary="會員角色"
                            secondary={user.role === 'admin' ? '管理員' : '一般會員'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="帳號狀態"
                            secondary={user.status === 'active' ? '正常' : '停用'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="註冊時間"
                            secondary={new Date(user.created_at).toLocaleString()}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="語言偏好"
                            secondary={preferences?.language === 'zh_TW' ? '繁體中文' : 'English'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="介面主題"
                            secondary={preferences?.theme === 'light' ? '淺色' : '深色'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="通知設定"
                            secondary={preferences?.notifications ? '開啟' : '關閉'}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem>
                        <ListItemText
                            primary="收貨地址"
                            secondary={
                                <Box>
                                    {addresses.map((address, index) => (
                                        <Typography key={index} variant="body2" color="text.secondary">
                                            {address.full_address}
                                        </Typography>
                                    ))}
                                </Box>
                            }
                        />
                    </ListItem>
                </List>

                <Box sx={{ mt: 4, display: 'flex', gap: 2 }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleEditClick}
                    >
                        編輯資料
                    </Button>
                    <Button
                        variant="outlined"
                        color="error"
                        onClick={handleLogout}
                    >
                        登出
                    </Button>
                    <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => setOpenPreferencesDialog(true)}
                    >
                        編輯偏好設定
                    </Button>
                </Box>
            </Paper>

            <Dialog open={openEditDialog} onClose={handleEditClose}>
                <DialogTitle>編輯個人資料</DialogTitle>
                <DialogContent>
                    <TextField
                        autoFocus
                        margin="dense"
                        label="用戶名稱"
                        type="text"
                        fullWidth
                        value={editForm.username}
                        onChange={(e) =>
                            setEditForm({ ...editForm, username: e.target.value })
                        }
                    />
                    <TextField
                        margin="dense"
                        label="電子郵件"
                        type="email"
                        fullWidth
                        value={editForm.email}
                        onChange={(e) =>
                            setEditForm({ ...editForm, email: e.target.value })
                        }
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleEditClose}>取消</Button>
                    <Button onClick={handleEditSubmit} variant="contained">
                        儲存
                    </Button>
                </DialogActions>
            </Dialog>

            <Dialog open={openPreferencesDialog} onClose={() => setOpenPreferencesDialog(false)}>
                <DialogTitle>編輯偏好設定</DialogTitle>
                <DialogContent>
                    <Box sx={{ mt: 2 }}>
                        <TextField
                            select
                            fullWidth
                            label="語言"
                            value={preferencesForm.language}
                            onChange={(e) =>
                                setPreferencesForm({ ...preferencesForm, language: e.target.value })
                            }
                            SelectProps={{
                                native: true,
                            }}
                        >
                            <option value="zh_TW">繁體中文</option>
                            <option value="en">English</option>
                        </TextField>
                    </Box>
                    <Box sx={{ mt: 2 }}>
                        <TextField
                            select
                            fullWidth
                            label="幣別"
                            value={preferencesForm.currency}
                            onChange={(e) =>
                                setPreferencesForm({ ...preferencesForm, currency: e.target.value })
                            }
                            SelectProps={{
                                native: true,
                            }}
                        >
                            <option value="TWD">新台幣 (TWD)</option>
                            <option value="USD">美元 (USD)</option>
                        </TextField>
                    </Box>
                    <Box sx={{ mt: 2 }}>
                        <TextField
                            select
                            fullWidth
                            label="主題"
                            value={preferencesForm.theme}
                            onChange={(e) =>
                                setPreferencesForm({ ...preferencesForm, theme: e.target.value })
                            }
                            SelectProps={{
                                native: true,
                            }}
                        >
                            <option value="light">淺色</option>
                            <option value="dark">深色</option>
                        </TextField>
                    </Box>
                    <Box sx={{ mt: 2 }}>
                        <TextField
                            select
                            fullWidth
                            label="電子郵件通知"
                            value={preferencesForm.notification_email ? 'true' : 'false'}
                            onChange={(e) =>
                                setPreferencesForm({
                                    ...preferencesForm,
                                    notification_email: e.target.value === 'true',
                                })
                            }
                            SelectProps={{
                                native: true,
                            }}
                        >
                            <option value="true">開啟</option>
                            <option value="false">關閉</option>
                        </TextField>
                    </Box>
                    <Box sx={{ mt: 2 }}>
                        <TextField
                            select
                            fullWidth
                            label="簡訊通知"
                            value={preferencesForm.notification_sms ? 'true' : 'false'}
                            onChange={(e) =>
                                setPreferencesForm({
                                    ...preferencesForm,
                                    notification_sms: e.target.value === 'true',
                                })
                            }
                            SelectProps={{
                                native: true,
                            }}
                        >
                            <option value="true">開啟</option>
                            <option value="false">關閉</option>
                        </TextField>
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenPreferencesDialog(false)}>取消</Button>
                    <Button onClick={handlePreferencesSubmit} variant="contained">
                        儲存
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default Profile;
