import React, { useState, useEffect } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import {
    AppBar,
    Box,
    Container,
    Toolbar,
    Typography,
    Button,
    IconButton,
    Badge,
    Menu,
    MenuItem,
    Avatar,
} from '@mui/material';
import {
    ShoppingCart as ShoppingCartIcon,
    Person as PersonIcon,
} from '@mui/icons-material';

const StoreLayout = () => {
    const navigate = useNavigate();
    const [anchorEl, setAnchorEl] = useState(null);
    const [user, setUser] = useState(null);

    useEffect(() => {
        // 檢查是否已登入
        const userToken = localStorage.getItem('userToken');
        const userData = localStorage.getItem('userData');

        if (userToken && userData) {
            setUser(JSON.parse(userData));
        }
    }, []);

    const handleMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    const handleLogout = () => {
        localStorage.removeItem('userToken');
        localStorage.removeItem('userData');
        setUser(null);
        handleMenuClose();
        navigate('/');
    };

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
            <AppBar position="fixed">
                <Toolbar>
                    <Typography
                        variant="h6"
                        component="div"
                        sx={{ flexGrow: 1, cursor: 'pointer' }}
                        onClick={() => navigate('/')}
                    >
                        訂單管理系統
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                        <Button color="inherit" onClick={() => navigate('/store/products')}>
                            所有商品
                        </Button>
                        <Button color="inherit" onClick={() => navigate('/store/categories')}>
                            商品分類
                        </Button>
                        <IconButton color="inherit" onClick={() => navigate('/store/cart')}>
                            <Badge badgeContent={0} color="error">
                                <ShoppingCartIcon />
                            </Badge>
                        </IconButton>

                        {user ? (
                            <>
                                <IconButton
                                    onClick={handleMenuOpen}
                                    sx={{
                                        padding: 0.5,
                                        border: '2px solid white',
                                        borderRadius: '50%',
                                    }}
                                >
                                    <Avatar
                                        alt={user.name}
                                        src={user.avatar}
                                        sx={{ width: 32, height: 32 }}
                                    >
                                        {user.name?.charAt(0)}
                                    </Avatar>
                                </IconButton>
                                <Menu
                                    anchorEl={anchorEl}
                                    open={Boolean(anchorEl)}
                                    onClose={handleMenuClose}
                                >
                                    <MenuItem onClick={() => {
                                        handleMenuClose();
                                        navigate('/store/profile');
                                    }}>
                                        個人資料
                                    </MenuItem>
                                    <MenuItem onClick={() => {
                                        handleMenuClose();
                                        navigate('/store/orders');
                                    }}>
                                        訂單記錄
                                    </MenuItem>
                                    <MenuItem onClick={() => {
                                        handleMenuClose();
                                        navigate('/store/wishlist');
                                    }}>
                                        收藏清單
                                    </MenuItem>
                                    <MenuItem onClick={handleLogout}>
                                        登出
                                    </MenuItem>
                                </Menu>
                            </>
                        ) : (
                            <Button
                                color="inherit"
                                onClick={() => navigate('/login')}
                                startIcon={<PersonIcon />}
                            >
                                登入
                            </Button>
                        )}
                    </Box>
                </Toolbar>
            </AppBar>
            <Box
                component="main"
                sx={{
                    flexGrow: 1,
                    bgcolor: 'background.default',
                    mt: ['48px', '56px', '64px'],
                    minHeight: '100vh',
                }}
            >
                <Outlet />
            </Box>
            <Box
                component="footer"
                sx={{
                    py: 3,
                    px: 2,
                    mt: 'auto',
                    backgroundColor: (theme) =>
                        theme.palette.mode === 'light'
                            ? theme.palette.grey[200]
                            : theme.palette.grey[800],
                }}
            >
                <Container maxWidth="lg">
                    <Typography variant="body2" color="text.secondary" align="center">
                        © {new Date().getFullYear()} 訂單管理系統. All rights reserved.
                    </Typography>
                </Container>
            </Box>
        </Box>
    );
};

export default StoreLayout;
