export const API_CONFIG = {
    // 各服務的基礎 URL
    SERVICES: {
        AUTH: process.env.REACT_APP_AUTH_SERVICE_URL || 'https://ordermanagersystem-auth-service.onrender.com',
        PRODUCT: process.env.REACT_APP_PRODUCT_SERVICE_URL || 'https://ordermanagersystem-product-service.onrender.com',
        CART: process.env.REACT_APP_CART_SERVICE_URL || 'https://ordermanagersystem-cart-service.onrender.com',
        ORDER: process.env.REACT_APP_ORDER_SERVICE_URL || 'https://ordermanagersystem-order-service.onrender.com',
    },

    // API 版本
    VERSION: 'v1',

    // 各服務的端點
    ENDPOINTS: {
        AUTH: {
            LOGIN: '/auth/login',
            REGISTER: '/auth/register',
            PREFERENCES: '/user/preferences',
        },
        PRODUCT: {
            LIST: '/products',
            DETAIL: (id) => `/products/${id}`,
            CATEGORIES: '/categories',
        },
        CART: {
            GET: '/cart',
            ADD: '/cart/items',
            UPDATE: (id) => `/cart/items/${id}`,
            REMOVE: (id) => `/cart/items/${id}`,
        },
        ORDER: {
            CREATE: '/orders',
            LIST: '/orders',
            DETAIL: (id) => `/orders/${id}`,
        }
    },

    // 請求超時設置
    TIMEOUT: 10000,

    // 請求頭設置
    HEADERS: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
};

// 構建完整的 API URL
export const getApiUrl = (endpoint) => `${API_CONFIG.SERVICES.AUTH}/api/${API_CONFIG.VERSION}${endpoint}`;

// 創建 axios 實例的配置
export const axiosConfig = {
    baseURL: `${API_CONFIG.SERVICES.AUTH}/api/${API_CONFIG.VERSION}`,
    timeout: API_CONFIG.TIMEOUT,
    headers: API_CONFIG.HEADERS,
};

// 導出默認配置
export default API_CONFIG;
