import { API_CONFIG } from '../config/api';
import axios from 'axios';

// 創建 axios 實例
const api = axios.create({
    baseURL: `${API_CONFIG.SERVICES.AUTH}/api/${API_CONFIG.VERSION}`,
    headers: {
        'Content-Type': 'application/json',
        'Accept': '*/*',
    }
});

// API 服務
export const apiService = {
    // 用戶偏好設置
    preferences: {
        get: () => api.get(API_CONFIG.ENDPOINTS.AUTH.PREFERENCES),
        update: (data) => api.put(API_CONFIG.ENDPOINTS.AUTH.PREFERENCES, data),
    },

    // 認證相關
    auth: {
        login: (credentials) => api.post(API_CONFIG.ENDPOINTS.AUTH.LOGIN, credentials),
        register: (userData) => api.post(API_CONFIG.ENDPOINTS.AUTH.REGISTER, userData),
    },
};

export default api;
