import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import theme from './theme';
import MainLayout from './components/Layout/MainLayout';
import Dashboard from './pages/Dashboard';

// 臨時頁面組件
const TemporaryPage = ({ title }) => (
    <div style={{ padding: 20 }}>
        <h2>{title}</h2>
        <p>此頁面正在開發中...</p>
    </div>
);

function App() {
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <MainLayout>
                <Routes>
                    <Route path="/" element={<Dashboard />} />
                    <Route path="/products" element={<TemporaryPage title="商品管理" />} />
                    <Route path="/categories" element={<TemporaryPage title="商品分類" />} />
                    <Route path="/orders" element={<TemporaryPage title="訂單管理" />} />
                    <Route path="/promotions" element={<TemporaryPage title="促銷活動" />} />
                    <Route path="/customers" element={<TemporaryPage title="會員管理" />} />
                    <Route path="/reports" element={<TemporaryPage title="銷售報表" />} />
                </Routes>
            </MainLayout>
        </ThemeProvider>
    );
}

export default App;
