import React, { useState, useEffect } from 'react';
import axios from 'axios';

import {
    Container,
    Grid,
    Paper,
    Typography,
    Box,
    Avatar,
    Button,
    Switch,
    FormControlLabel,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    Alert,
    Snackbar,
    Tab,
    Tabs,
    CircularProgress,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    IconButton,
    List,
    ListItem,
    ListItemText,
    ListItemSecondaryAction,
} from '@mui/material';
import {
    Settings,
    Notifications,
    Add as AddIcon,
    Edit as EditIcon,
    Delete as DeleteIcon,
    Home as HomeIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

// 設定面板內容組件
function TabPanel({ children, value, index }) {
    return (
        <div role="tabpanel" hidden={value !== index}>
            {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
        </div>
    );
}

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [tabValue, setTabValue] = useState(0);
    const [preferences, setPreferences] = useState({
        language: 'zh-TW',
        currency: 'TWD',
        theme: 'light',
        notification_email: true,
        notification_sms: false,
    });
    const [addresses, setAddresses] = useState([]);
    const [openAddressDialog, setOpenAddressDialog] = useState(false);
    const [currentAddress, setCurrentAddress] = useState(null);
    const [addressForm, setAddressForm] = useState({
        name: '',
        phone: '',
        street: '',
        city: '',
        district: '',
        postal_code: '',
        is_default: false,
    });

    // 新增：獲取用戶信息
    const fetchUserInfo = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:8083/api/v1/user/profile', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            setUser(response.data);
        } catch (err) {
            console.error('獲取用戶信息失敗:', err);
            setError('無法載入用戶信息');
        }
    };

    useEffect(() => {
        fetchUserInfo();
        fetchUserPreferences();
        fetchAddresses();
    }, []);

    const fetchUserPreferences = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:8083/api/v1/user/preferences', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            if (response && response.data) {
                setPreferences(response.data);
            }
        } catch (err) {
            console.error('獲取用戶設定失敗:', err);
            setError('無法載入用戶設定');
        } finally {
            setLoading(false);
        }
    };

    const fetchAddresses = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:8083/api/v1/user/addresses', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            if (response && response.data) {
                setAddresses(response.data);
            }
        } catch (err) {
            console.error('獲取地址列表失敗:', err);
            if (err.response) {
                setError(`無法載入地址列表: ${err.response.data.message}`);
            } else if (err.request) {
                setError('網絡連接失敗，請檢查您的網絡連接');
            } else {
                setError('發生未知錯誤');
            }
        } finally {
            setLoading(false);
        }
    };

    const handlePreferencesChange = async () => {
        try {
            const token = localStorage.getItem('token');
            await axios.put('http://localhost:8083/api/v1/user/preferences', preferences, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            setSuccess('設定已更新');
        } catch (err) {
            console.error('更新用戶設定失敗:', err);
            setError('無法更新設定');
        }
    };

    const handleTabChange = (event, newValue) => {
        setTabValue(newValue);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    const handleAddressDialogOpen = (address = null) => {
        if (address) {
            setCurrentAddress(address);
            setAddressForm(address);
        } else {
            setCurrentAddress(null);
            setAddressForm({
                name: '',
                phone: '',
                street: '',
                city: '',
                district: '',
                postal_code: '',
                is_default: false,
            });
        }
        setOpenAddressDialog(true);
    };

    const handleAddressSubmit = async () => {
        try {
            const token = localStorage.getItem('token');
            if (currentAddress) {
                await axios.put(`http://localhost:8083/api/v1/user/addresses/${currentAddress.id}`, 
                    addressForm,
                    {
                        headers: {
                            'Authorization': `Bearer ${token}`,
                            'Content-Type': 'application/json'
                        }
                    }
                );
                setSuccess('地址已更新');
            } else {
                await axios.post('http://localhost:8083/api/v1/user/addresses', 
                    addressForm,
                    {
                        headers: {
                            'Authorization': `Bearer ${token}`,
                            'Content-Type': 'application/json'
                        }
                    }
                );
                setSuccess('地址已新增');
            }
            setOpenAddressDialog(false);
            fetchAddresses();
        } catch (err) {
            setError('操作失敗');
        }
    };

    const handleDeleteAddress = async (id) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:8083/api/v1/user/addresses/${id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            setSuccess('地址已刪除');
            fetchAddresses();
        } catch (err) {
            setError('刪除失敗');
        }
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <Grid container spacing={3}>
                {/* 個人資料卡片 */}
                <Grid item xs={12} md={4}>
                    <Paper
                        sx={{
                            p: 3,
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}
                    >
                        <Avatar
                            sx={{
                                width: 100,
                                height: 100,
                                mb: 2,
                                bgcolor: 'primary.main',
                                fontSize: '2.5rem',
                            }}
                        >
                            {user?.name?.[0]?.toUpperCase()}
                        </Avatar>
                        <Typography variant="h5" gutterBottom>
                            {user?.name}
                        </Typography>
                        <Typography color="text.secondary" gutterBottom>
                            {user?.email}
                        </Typography>
                        <Typography
                            variant="body2"
                            color="text.secondary"
                            sx={{ mb: 2 }}
                        >
                            {user?.role === 'admin' ? '管理員' : '一般會員'}
                        </Typography>
                        <Button
                            variant="outlined"
                            color="error"
                            onClick={handleLogout}
                            sx={{ mt: 2 }}
                        >
                            登出
                        </Button>
                    </Paper>
                </Grid>

                {/* 設定面板 */}
                <Grid item xs={12} md={8}>
                    <Paper sx={{ width: '100%' }}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                            <Tabs
                                value={tabValue}
                                onChange={handleTabChange}
                                aria-label="設定選項"
                            >
                                <Tab
                                    icon={<Settings />}
                                    label="一般設定"
                                    iconPosition="start"
                                />
                                <Tab
                                    icon={<Notifications />}
                                    label="通知設定"
                                    iconPosition="start"
                                />
                                <Tab
                                    icon={<HomeIcon />}
                                    label="地址管理"
                                    iconPosition="start"
                                />
                            </Tabs>
                        </Box>

                        <TabPanel value={tabValue} index={0}>
                            <Grid container spacing={3}>
                                <Grid item xs={12} sm={6}>
                                    <FormControl fullWidth>
                                        <InputLabel>語言</InputLabel>
                                        <Select
                                            value={preferences.language}
                                            label="語言"
                                            onChange={(e) =>
                                                setPreferences({
                                                    ...preferences,
                                                    language: e.target.value,
                                                })
                                            }
                                        >
                                            <MenuItem value="zh-TW">繁體中文</MenuItem>
                                            <MenuItem value="en-US">English</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <FormControl fullWidth>
                                        <InputLabel>貨幣</InputLabel>
                                        <Select
                                            value={preferences.currency}
                                            label="貨幣"
                                            onChange={(e) =>
                                                setPreferences({
                                                    ...preferences,
                                                    currency: e.target.value,
                                                })
                                            }
                                        >
                                            <MenuItem value="TWD">新台幣 (TWD)</MenuItem>
                                            <MenuItem value="USD">美元 (USD)</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid>
                                <Grid item xs={12}>
                                    <FormControlLabel
                                        control={
                                            <Switch
                                                checked={preferences.theme === 'dark'}
                                                onChange={(e) =>
                                                    setPreferences({
                                                        ...preferences,
                                                        theme: e.target.checked ? 'dark' : 'light',
                                                    })
                                                }
                                            />
                                        }
                                        label="深色模式"
                                    />
                                </Grid>
                            </Grid>
                        </TabPanel>

                        <TabPanel value={tabValue} index={1}>
                            <Grid container spacing={3}>
                                <Grid item xs={12}>
                                    <FormControlLabel
                                        control={
                                            <Switch
                                                checked={preferences.notification_email}
                                                onChange={(e) =>
                                                    setPreferences({
                                                        ...preferences,
                                                        notification_email: e.target.checked,
                                                    })
                                                }
                                            />
                                        }
                                        label="接收電子郵件通知"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <FormControlLabel
                                        control={
                                            <Switch
                                                checked={preferences.notification_sms}
                                                onChange={(e) =>
                                                    setPreferences({
                                                        ...preferences,
                                                        notification_sms: e.target.checked,
                                                    })
                                                }
                                            />
                                        }
                                        label="接收簡訊通知"
                                    />
                                </Grid>
                            </Grid>
                        </TabPanel>

                        <TabPanel value={tabValue} index={2}>
                            <Box sx={{ mb: 2 }}>
                                <Button
                                    variant="contained"
                                    startIcon={<AddIcon />}
                                    onClick={() => handleAddressDialogOpen()}
                                >
                                    新增地址
                                </Button>
                            </Box>

                            <List>
                                {addresses.map((address) => (
                                    <ListItem
                                        key={address.id}
                                        sx={{
                                            border: '1px solid',
                                            borderColor: 'divider',
                                            borderRadius: 1,
                                            mb: 1,
                                        }}
                                    >
                                        <ListItemText
                                            primary={
                                                <Typography variant="subtitle1">
                                                    {address.name} {address.is_default && '(預設)'}
                                                </Typography>
                                            }
                                            secondary={
                                                <>
                                                    <Typography variant="body2">
                                                        電話：{address.phone}
                                                    </Typography>
                                                    <Typography variant="body2">
                                                        地址：{address.postal_code} {address.city}{address.district}{address.street}
                                                    </Typography>
                                                </>
                                            }
                                        />
                                        <ListItemSecondaryAction>
                                            <IconButton
                                                edge="end"
                                                onClick={() => handleAddressDialogOpen(address)}
                                            >
                                                <EditIcon />
                                            </IconButton>
                                            <IconButton
                                                edge="end"
                                                onClick={() => handleDeleteAddress(address.id)}
                                            >
                                                <DeleteIcon />
                                            </IconButton>
                                        </ListItemSecondaryAction>
                                    </ListItem>
                                ))}
                            </List>
                        </TabPanel>

                        <Box sx={{ p: 3, display: 'flex', justifyContent: 'flex-end' }}>
                            <Button
                                variant="contained"
                                onClick={handlePreferencesChange}
                            >
                                儲存設定
                            </Button>
                        </Box>
                    </Paper>
                </Grid>
            </Grid>

            <Snackbar
                open={!!error}
                autoHideDuration={6000}
                onClose={() => setError('')}
            >
                <Alert severity="error" onClose={() => setError('')}>
                    {error}
                </Alert>
            </Snackbar>

            <Snackbar
                open={!!success}
                autoHideDuration={6000}
                onClose={() => setSuccess('')}
            >
                <Alert severity="success" onClose={() => setSuccess('')}>
                    {success}
                </Alert>
            </Snackbar>

            {/* 地址編輯對話框 */}
            <Dialog
                open={openAddressDialog}
                onClose={() => setOpenAddressDialog(false)}
                maxWidth="sm"
                fullWidth
            >
                <DialogTitle>
                    {currentAddress ? '編輯地址' : '新增地址'}
                </DialogTitle>
                <DialogContent>
                    <Grid container spacing={2} sx={{ mt: 1 }}>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="收件人姓名"
                                value={addressForm.name}
                                onChange={(e) =>
                                    setAddressForm({
                                        ...addressForm,
                                        name: e.target.value,
                                    })
                                }
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="聯絡電話"
                                value={addressForm.phone}
                                onChange={(e) =>
                                    setAddressForm({
                                        ...addressForm,
                                        phone: e.target.value,
                                    })
                                }
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="城市"
                                value={addressForm.city}
                                onChange={(e) =>
                                    setAddressForm({
                                        ...addressForm,
                                        city: e.target.value,
                                    })
                                }
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="地區"
                                value={addressForm.district}
                                onChange={(e) =>
                                    setAddressForm({
                                        ...addressForm,
                                        district: e.target.value,
                                    })
                                }
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="郵遞區號"
                                value={addressForm.postal_code}
                                onChange={(e) =>
                                    setAddressForm({
                                        ...addressForm,
                                        postal_code: e.target.value,
                                    })
                                }
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label="詳細地址"
                                value={addressForm.street}
                                onChange={(e) =>
                                    setAddressForm({
                                        ...addressForm,
                                        street: e.target.value,
                                    })
                                }
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <FormControlLabel
                                control={
                                    <Switch
                                        checked={addressForm.is_default}
                                        onChange={(e) =>
                                            setAddressForm({
                                                ...addressForm,
                                                is_default: e.target.checked,
                                            })
                                        }
                                    />
                                }
                                label="設為預設地址"
                            />
                        </Grid>
                    </Grid>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenAddressDialog(false)}>
                        取消
                    </Button>
                    <Button
                        variant="contained"
                        onClick={handleAddressSubmit}
                    >
                        確認
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default Profile;
